import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function App() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({
    name: '',
    description: '',
    images: [''],
    regularPrice: '',
    salePrice: '',
    stockStatus: 'In Stock',
    stockQuantity: '',
    weight: '',
    dimensions: { length: '', width: '', height: '' }
  });
  const [editId, setEditId] = useState(null);

  const fetchItems = async () => {
    const res = await axios.get('http://localhost:5000/api/items');
    setItems(res.data);
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editId) {
      await axios.put(`http://localhost:5000/api/items/${editId}`, form);
    } else {
      await axios.post('http://localhost:5000/api/items', form);
    }
    setForm({
      name: '',
      description: '',
      images: [''],
      regularPrice: '',
      salePrice: '',
      stockStatus: 'In Stock',
      stockQuantity: '',
      weight: '',
      dimensions: { length: '', width: '', height: '' }
    });
    setEditId(null);
    fetchItems();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/items/${id}`);
    fetchItems();
  };

  const handleEdit = (item) => {
    setForm(item);
    setEditId(item._id);
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Grocery Admin Dashboard - Inventory</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <input placeholder="Name" value={form.name} 
               onChange={e => setForm({ ...form, name: e.target.value })} req/><br />
        <textarea placeholder="Description" value={form.description} 
                  onChange={e => setForm({ ...form, description: e.target.value })}></textarea><br />
        <input placeholder="Google Drive Image Link" value={form.images[0]} 
               onChange={e => setForm({ ...form, images: [e.target.value] })} /><br />
        <input placeholder="Regular Price" type="number" value={form.regularPrice} 
               onChange={e => setForm({ ...form, regularPrice: e.target.value })} /><br />
        <input placeholder="Sale Price" type="number" value={form.salePrice} 
               onChange={e => setForm({ ...form, salePrice: e.target.value })} /><br />
        <input placeholder="Stock Quantity" type="number" value={form.stockQuantity} 
               onChange={e => setForm({ ...form, stockQuantity: e.target.value })} /><br />
        <input placeholder="Weight" value={form.weight} 
               onChange={e => setForm({ ...form, weight: e.target.value })} /><br />
        <input placeholder="Length" type="number" value={form.dimensions.length} 
               onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, length: e.target.value } })} /><br />
        <input placeholder="Width" type="number" value={form.dimensions.width} 
               onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, width: e.target.value } })} /><br />
        <input placeholder="Height" type="number" value={form.dimensions.height} 
               onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, height: e.target.value } })} /><br />

        <select value={form.stockStatus} onChange={e => setForm({ ...form, stockStatus: e.target.value })}>
          <option>In Stock</option>
          <option>Out of Stock</option>
        </select><br />

        <button type="submit">{editId ? 'Update' : 'Add'} Item</button>
      </form>

      <h2>Inventory Items</h2>
      <ul>
        {items.map(item => (
          <li key={item._id}>
            <b>{item.name}</b> - {item.stockStatus} - {item.regularPrice}  
            {item.images[0] && <a href={item.images[0]} target="_blank" rel="noreferrer"> [Image]</a>}
            <button onClick={() => handleEdit(item)}>Edit</button>
            <button onClick={() => handleDelete(item._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
