import React, { useEffect, useState, createContext, useContext } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ShoppingCart, X, Search, Plus } from 'lucide-react';
import axios from 'axios';

// Default export App (single-file previewable React component)
export default function App() {
  return (
    <CartProvider>
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 text-gray-800">
        <Header />
        <main className="max-w-7xl mx-auto px-6 py-10">
          <Dashboard />
        </main>
        <CartDrawer />
      </div>
    </CartProvider>
  );
}

/* -------------------- Cart Context -------------------- */
const CartContext = createContext();

function CartProvider({ children }) {
  const [items, setItems] = useState([]);
  const [isOpen, setIsOpen] = useState(false);

  const add = (product) => {
    setItems((curr) => {
      const found = curr.find((p) => p._id === product._id);
      if (found) return curr.map((p) => p._id === product._id ? { ...p, qty: p.qty + 1 } : p);
      return [{ ...product, qty: 1 }, ...curr];
    });
    setIsOpen(true);
  };
  const remove = (id) => setItems((curr) => curr.filter((p) => p._id !== id));
  const changeQty = (id, qty) => setItems((curr) => curr.map(p => p._id === id ? { ...p, qty } : p));
  const clear = () => setItems([]);

  const total = items.reduce(
  (s, p) => s + (Number((p.salePrice ?? p.regularPrice) || 0) * p.qty),
  0
  );

  return (
    <CartContext.Provider value={{ items, add, remove, changeQty, clear, isOpen, setIsOpen, total }}>
      {children}
    </CartContext.Provider>
  );
}

function useCart() { return useContext(CartContext); }

/* -------------------- Header -------------------- */
function Header() {
  const { items, setIsOpen } = useCart();
  return (
    <header className="sticky top-0 z-40 backdrop-blur bg-white/60 border-b">
      <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
        <div className="flex items-center gap-4">
          <div className="rounded-lg bg-emerald-600 text-white px-3 py-2 text-xl font-semibold shadow-md">Grocery</div>
          <nav className="hidden md:flex gap-6 text-sm font-medium text-gray-600">
            <a className="hover:text-gray-900">Inventory</a>
            <a className="hover:text-gray-900">Analytics</a>
            <a className="hover:text-gray-900">Orders</a>
          </nav>
        </div>

        <div className="flex items-center gap-3">
          <SearchBar />
          <button onClick={() => setIsOpen(true)} className="relative flex items-center gap-2 px-3 py-2 border rounded-md hover:shadow">
            <ShoppingCart size={18} />
            <span className="text-sm font-medium">Cart</span>
            {items.length > 0 && (
              <span className="absolute -right-2 -top-2 bg-rose-500 text-white text-xs px-2 py-0.5 rounded-full shadow">{items.length}</span>
            )}
          </button>
        </div>
      </div>
    </header>
  );
}

/* -------------------- Search Bar -------------------- */
function SearchBar() {
  const [q, setQ] = useState('');
  // notify parent via event - simple approach using custom event
  useEffect(() => {
    const event = new CustomEvent('searchQuery', { detail: q });
    window.dispatchEvent(event);
  }, [q]);

  return (
    <div className="flex items-center border rounded-lg px-2 py-1 bg-white shadow-sm">
      <Search size={16} className="text-gray-500" />
      <input value={q} onChange={e => setQ(e.target.value)} placeholder="Search items, e.g., Rice, Oil..." className="ml-2 outline-none text-sm" />
    </div>
  );
}

/* -------------------- Dashboard (Products + Filters) -------------------- */
function Dashboard() {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('grid');
  const [sort, setSort] = useState('newest');

  useEffect(() => {
    fetchProducts();
    const onSearch = (e) => {
      const q = e.detail?.toLowerCase?.() || '';
      setFiltered(products.filter(p => p.name.toLowerCase().includes(q) || (p.description||'').toLowerCase().includes(q)));
    };
    window.addEventListener('searchQuery', onSearch);
    return () => window.removeEventListener('searchQuery', onSearch);
  }, [products]);

  useEffect(() => {
    applySort();
  }, [sort, products]);

  const fetchProducts = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:5000/api/items');
      setProducts(res.data.map(d => ({ ...d })));
      setFiltered(res.data.map(d => ({ ...d })));
    } catch (err) {
      console.error(err);
    } finally { setLoading(false); }
  };

  const applySort = () => {
    const copy = [...(filtered.length ? filtered : products)];
    if (sort === 'price-low') copy.sort((a,b) => (a.salePrice ?? a.regularPrice) - (b.salePrice ?? b.regularPrice));
    else if (sort === 'price-high') copy.sort((a,b) => (b.salePrice ?? b.regularPrice) - (a.salePrice ?? a.regularPrice));
    else if (sort === 'newest') copy.sort((a,b) => new Date(b.timestamp) - new Date(a.timestamp));
    setFiltered(copy);
  };

  return (
    <section>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold">Inventory</h2>
          <p className="text-sm text-gray-500 mt-1">Manage product catalog, pricing and stock.</p>
        </div>
        <div className="flex items-center gap-3">
          <div className="flex items-center gap-2 border rounded-md px-3 py-2 bg-white shadow-sm">
            <label className="text-sm text-gray-600">View</label>
            <select value={view} onChange={e => setView(e.target.value)} className="ml-2 outline-none text-sm">
              <option value="grid">Grid</option>
              <option value="list">List</option>
            </select>
          </div>
          <div className="flex items-center gap-2 border rounded-md px-3 py-2 bg-white shadow-sm">
            <label className="text-sm text-gray-600">Sort</label>
            <select value={sort} onChange={e => setSort(e.target.value)} className="ml-2 outline-none text-sm">
              <option value="newest">Newest</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
            </select>
          </div>
          <AddItemButton onAdd={() => {/* will open modal - controlled inside AddItemButton */}} />
        </div>
      </div>

      <div>
        {loading ? (
          <div className="h-64 grid place-items-center text-gray-400">Loading products...</div>
        ) : (
          <ProductGrid products={filtered} view={view} refresh={fetchProducts} />
        )}
      </div>
    </section>
  );
}

