/* -------------------- Item Form Modal (Create / Update) -------------------- */
function ItemFormModal({ onClose, defaultValues = {} }) {
  const [form, setForm] = useState({
    name: defaultValues.name || '',
    description: defaultValues.description || '',
    images: defaultValues.images || [''],
    regularPrice: defaultValues.regularPrice || '',
    salePrice: defaultValues.salePrice || '',
    stockQuantity: defaultValues.stockQuantity || 0,
    weight: defaultValues.weight || '',
    dimensions: defaultValues.dimensions || { length: '', width: '', height: '' }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (defaultValues._id) {
        await axios.put(`http://localhost:5000/api/items/${defaultValues._id}`, form);
      } else {
        await axios.post('http://localhost:5000/api/items', form);
      }
      if (onClose) onClose();
    } catch (err) { console.error(err); }
  };

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-50 grid place-items-center">
      <div className="absolute inset-0 bg-black/40" onClick={onClose} />
      <motion.form onSubmit={handleSubmit} initial={{ y: 30 }} animate={{ y: 0 }} exit={{ y: 30 }} className="relative z-10 bg-white w-full max-w-2xl rounded-2xl p-6 shadow-2xl">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold">{defaultValues._id ? 'Edit Item' : 'Add New Item'}</h3>
          <button type="button" onClick={onClose}><X /></button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input required placeholder="Name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} className="border rounded-md px-3 py-2" />
          <input placeholder="Regular Price" type="number" value={form.regularPrice} onChange={e => setForm({ ...form, regularPrice: e.target.value })} className="border rounded-md px-3 py-2" />
          <textarea placeholder="Description" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} className="border rounded-md px-3 py-2 md:col-span-2" />

          <div className="md:col-span-2">
            <label className="text-sm text-gray-600">Google Drive Image Links (one per line)</label>
            <textarea value={(form.images || []).join('\n')} onChange={e => setForm({ ...form, images: e.target.value.split('\n').filter(Boolean) })} className="w-full border rounded-md px-3 py-2 mt-1" />
          </div>

          <input placeholder="Sale Price (optional)" type="number" value={form.salePrice} onChange={e => setForm({ ...form, salePrice: e.target.value })} className="border rounded-md px-3 py-2" />

          <input placeholder="Stock Quantity" type="number" value={form.stockQuantity} onChange={e => setForm({ ...form, stockQuantity: e.target.value })} className="border rounded-md px-3 py-2" />

          <input placeholder="Weight (e.g., 500g)" value={form.weight} onChange={e => setForm({ ...form, weight: e.target.value })} className="border rounded-md px-3 py-2" />

          <div className="flex gap-2">
            <input placeholder="Length (cm)" type="number" value={form.dimensions.length} onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, length: e.target.value } })} className="border rounded-md px-3 py-2" />
            <input placeholder="Width (cm)" type="number" value={form.dimensions.width} onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, width: e.target.value } })} className="border rounded-md px-3 py-2" />
            <input placeholder="Height (cm)" type="number" value={form.dimensions.height} onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, height: e.target.value } })} className="border rounded-md px-3 py-2" />
          </div>
        </div>

        <div className="mt-6 flex items-center justify-end gap-3">
          <button type="button" onClick={onClose} className="px-4 py-2 border rounded-md">Cancel</button>
          <button type="submit" className="px-5 py-2 bg-emerald-600 text-white rounded-md">Save</button>
        </div>
      </motion.form>
    </motion.div>
  );
}

