import React, { useEffect, useState, createContext, useContext } from 'react';
import { ShoppingCart, X, Search, Plus, Trash2 } from 'lucide-react';

// Single-file React component (no framer-motion / no animations library)
// Requires: Tailwind CSS + lucide-react in your project

export default function App() {
  return (
    <CartProvider>
      <div className="min-h-screen bg-gradient-to-b from-gray-50 via-white to-gray-100 text-gray-800">
        <Header />
        <main className="max-w-7xl mx-auto px-6 py-10">
          <Dashboard />
        </main>
        <CartDrawer />
      </div>
    </CartProvider>
  );
}

/* -------------------- Cart Context -------------------- */
const CartContext = createContext();

function CartProvider({ children }) {
  const [items, setItems] = useState([]);
  const [isOpen, setIsOpen] = useState(false);

  const add = (product) => {
    setItems((curr) => {
      const found = curr.find((p) => p._id === product._id);
      if (found) return curr.map((p) => p._id === product._id ? { ...p, qty: p.qty + 1 } : p);
      return [{ ...product, qty: 1 }, ...curr];
    });
    setIsOpen(true);
  };
  const remove = (id) => setItems((curr) => curr.filter((p) => p._id !== id));
  const changeQty = (id, qty) => setItems((curr) => curr.map(p => p._id === id ? { ...p, qty } : p));
  const clear = () => setItems([]);

  const total = items.reduce(
    (s, p) => s + (Number((p.salePrice ?? p.regularPrice) || 0) * p.qty),
    0
  );

  return (
    <CartContext.Provider value={{ items, add, remove, changeQty, clear, isOpen, setIsOpen, total }}>
      {children}
    </CartContext.Provider>
  );
}

function useCart() { return useContext(CartContext); }

