/* -------------------- Search Bar -------------------- */
function SearchBar() {
  const [q, setQ] = useState('');
  useEffect(() => {
    const event = new CustomEvent('searchQuery', { detail: q });
    window.dispatchEvent(event);
  }, [q]);

  return (
    <div className="flex items-center border rounded-lg px-2 py-1 bg-white shadow-sm transition-all duration-200">
      <Search size={16} className="text-gray-500" />
      <input value={q} onChange={e => setQ(e.target.value)} placeholder="Search items, e.g., Rice, Oil..." className="ml-2 outline-none text-sm w-48 bg-transparent placeholder-gray-400" />
    </div>
  );
}

/* -------------------- Dashboard (Products + Filters) -------------------- */
function Dashboard() {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('grid');
  const [sort, setSort] = useState('newest');

  useEffect(() => {
    fetchProducts();
    const onSearch = (e) => {
      const q = e.detail?.toLowerCase?.() || '';
      setFiltered(products.filter(p => p.name.toLowerCase().includes(q) || (p.description||'').toLowerCase().includes(q)));
    };
    window.addEventListener('searchQuery', onSearch);
    return () => window.removeEventListener('searchQuery', onSearch);
  }, [products]);

  useEffect(() => { applySort(); }, [sort, products]);

  const fetchProducts = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/items');
      if (response.ok) {
        const data = await response.json();
        setProducts(data.map(d => ({ ...d })));
        setFiltered(data.map(d => ({ ...d })));
      } else {
        console.error('Failed to fetch products');
        setProducts([]);
        setFiltered([]);
      }
    } catch (err) {
      console.error('Error fetching products:', err);
      setProducts([]);
      setFiltered([]);
    } finally { setLoading(false); }
  };

  const applySort = () => {
    const copy = [...(filtered.length ? filtered : products)];
    if (sort === 'price-low') copy.sort((a,b) => (a.salePrice ?? a.regularPrice) - (b.salePrice ?? b.regularPrice));
    else if (sort === 'price-high') copy.sort((a,b) => (b.salePrice ?? b.regularPrice) - (a.salePrice ?? a.regularPrice));
    else if (sort === 'newest') copy.sort((a,b) => new Date(b.timestamp) - new Date(a.timestamp));
    setFiltered(copy);
  };

  return (
    <section>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold">Inventory</h2>
          <p className="text-sm text-gray-500 mt-1">Manage product catalog, pricing and stock.</p>
        </div>
        <div className="flex items-center gap-3">
          <div className="flex items-center gap-2 border rounded-md px-3 py-2 bg-white shadow-sm">
            <label className="text-sm text-gray-600">View</label>
            <select value={view} onChange={e => setView(e.target.value)} className="ml-2 outline-none text-sm bg-transparent">
              <option value="grid">Grid</option>
              <option value="list">List</option>
            </select>
          </div>
          <div className="flex items-center gap-2 border rounded-md px-3 py-2 bg-white shadow-sm">
            <label className="text-sm text-gray-600">Sort</label>
            <select value={sort} onChange={e => setSort(e.target.value)} className="ml-2 outline-none text-sm bg-transparent">
              <option value="newest">Newest</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
            </select>
          </div>
          <AddItemButton onAdd={() => {}} />
        </div>
      </div>

      <div>
        {loading ? (
          <div className="h-64 grid place-items-center text-gray-400">Loading products...</div>
        ) : (
          <ProductGrid products={filtered} view={view} refresh={fetchProducts} />
        )}
      </div>
    </section>
  );
}

/* -------------------- Add Item Button / Modal Trigger -------------------- */
function AddItemButton() {
  const [open, setOpen] = useState(false);
  const onClose = () => setOpen(false);
  return (
    <>
      <button onClick={() => setOpen(true)} className="flex items-center gap-2 bg-emerald-600 text-white px-4 py-2 rounded-md shadow hover:scale-[1.01] transition-transform duration-150">
        <Plus size={16} /> <span className="text-sm font-medium">Add Item</span>
      </button>
      {open && <ItemFormModal onClose={onClose} />}
    </>
  );
}

/* -------------------- Product Grid & List -------------------- */
function ProductGrid({ products = [], view = 'grid', refresh }) {
  return (
    <div className={view === 'grid' ? 'grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6' : 'flex flex-col gap-3'}>
      {products.map(p => (
        <ProductCard key={p._id} product={p} view={view} refresh={refresh} />
      ))}
    </div>
  );
}

function ProductCard({ product, view, refresh }) {
  const { add } = useCart();
  const price = product.salePrice ?? product.regularPrice;

  return (
    <div className="bg-white rounded-2xl p-4 shadow hover:shadow-md border transition-shadow duration-200">
      <div className={view === 'grid' ? '' : 'flex items-center gap-4'}>
        <div className={view === 'grid' ? 'h-44 rounded-lg overflow-hidden bg-gray-50 flex items-center justify-center' : 'w-28 h-20 rounded-lg overflow-hidden bg-gray-50 flex items-center justify-center'}>
          {product.images && product.images[0] ? (
            <img src={product.images[0]} alt={product.name} className="object-contain w-full h-full" />
          ) : (
            <div className="text-gray-300">No Image</div>
          )}
        </div>

        <div className={view === 'grid' ? 'mt-4' : 'flex-1'}>
          <div className="flex items-start justify-between">
            <div>
              <h3 className="text-lg font-semibold">{product.name}</h3>
              <p className="text-sm text-gray-500 mt-1 line-clamp-2">{product.description}</p>
            </div>
            <div className="text-right">
              <div className="text-xl font-semibold">{price}</div>
              <div className={`text-sm ${product.stockQuantity > 0 ? 'text-emerald-600' : 'text-rose-500'}`}>
                {product.stockQuantity > 0 ? `In stock (${product.stockQuantity})` : 'Out of stock'}
              </div>
            </div>
          </div>

          <div className="mt-4 flex items-center gap-3">
            <button onClick={() => add(product)} className="px-4 py-2 bg-emerald-600 text-white rounded-md shadow hover:bg-emerald-700 transition-colors duration-150">Add to Cart</button>
            <EditDeleteButtons product={product} refresh={refresh} />
          </div>
        </div>
      </div>
    </div>
  );
}

/* -------------------- Custom Confirmation Modal -------------------- */
function ConfirmationModal({ isOpen, onConfirm, onCancel, title, message }) {
  if (!isOpen) return null;
  return (
    <div className="fixed inset-0 z-50 grid place-items-center">
      <div className="absolute inset-0 bg-black/40" onClick={onCancel} />
      <div className="relative z-10 bg-white rounded-2xl p-6 shadow-2xl max-w-md w-full mx-4 transform transition-transform duration-200">
        <div className="text-center">
          <div className="mx-auto w-12 h-12 bg-rose-100 rounded-full flex items-center justify-center mb-4">
            <Trash2 size={24} className="text-rose-600" />
          </div>
          <h3 className="text-lg font-semibold mb-2">{title}</h3>
          <p className="text-gray-600 mb-6">{message}</p>
          <div className="flex items-center gap-3 justify-center">
            <button onClick={onCancel} className="px-4 py-2 border rounded-md hover:bg-gray-50">Cancel</button>
            <button onClick={onConfirm} className="px-4 py-2 bg-rose-600 text-white rounded-md hover:bg-rose-700">Delete</button>
          </div>
        </div>
      </div>
    </div>
  );
}

/* -------------------- Edit / Delete Buttons -------------------- */
function EditDeleteButtons({ product, refresh }) {
  const [editOpen, setEditOpen] = useState(false);
  const [confirmOpen, setConfirmOpen] = useState(false);

  const handleDelete = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/items/${product._id}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        refresh();
        setConfirmOpen(false);
      } else {
        console.error('Failed to delete item');
      }
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  return (
    <div className="flex items-center gap-2">
      <button onClick={() => setEditOpen(true)} className="px-3 py-1 border rounded-md text-sm hover:bg-gray-50">Edit</button>
      <button onClick={() => setConfirmOpen(true)} className="px-3 py-1 border rounded-md text-sm text-rose-600 hover:bg-rose-50">Delete</button>

      {editOpen && <ItemFormModal defaultValues={product} onClose={() => { setEditOpen(false); refresh(); }} />}

      <ConfirmationModal
        isOpen={confirmOpen}
        onConfirm={handleDelete}
        onCancel={() => setConfirmOpen(false)}
        title="Delete Item"
        message={`Are you sure you want to delete "${product.name}"? This action cannot be undone.`}
      />
    </div>
  );
}

/* -------------------- Item Form Modal (Create / Update) -------------------- */
function ItemFormModal({ onClose, defaultValues = {} }) {
  const [form, setForm] = useState({
    name: defaultValues.name || '',
    description: defaultValues.description || '',
    images: defaultValues.images || [''],
    regularPrice: defaultValues.regularPrice || '',
    salePrice: defaultValues.salePrice || '',
    stockQuantity: defaultValues.stockQuantity || 0,
    weight: defaultValues.weight || '',
    dimensions: defaultValues.dimensions || { length: '', width: '', height: '' }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = defaultValues._id ?
        `http://localhost:5000/api/items/${defaultValues._id}` :
        'http://localhost:5000/api/items';
      const method = defaultValues._id ? 'PUT' : 'POST';
      const response = await fetch(url, {
        method: method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      if (response.ok) {
        if (onClose) onClose();
      } else {
        console.error('Failed to save item');
      }
    } catch (err) {
      console.error('Error saving item:', err);
    }
  };

  return (
    <div className="fixed inset-0 z-50 grid place-items-center">
      <div className="absolute inset-0 bg-black/40" onClick={onClose} />
      <form onSubmit={handleSubmit} className="relative z-10 bg-white w-full max-w-2xl rounded-2xl p-6 shadow-2xl max-h-[90vh] overflow-y-auto transform transition-transform duration-200">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold">{defaultValues._id ? 'Edit Item' : 'Add New Item'}</h3>
          <button type="button" onClick={onClose} className="p-2 hover:bg-gray-100 rounded-full"><X /></button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input required placeholder="Name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />
          <input placeholder="Regular Price" type="number" value={form.regularPrice} onChange={e => setForm({ ...form, regularPrice: e.target.value })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />
          <textarea placeholder="Description" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} className="border rounded-lg px-3 py-2 md:col-span-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />

          <div className="md:col-span-2">
            <label className="text-sm text-gray-600">Google Drive Image Links (one per line)</label>
            <textarea value={(form.images || []).join('\n')} onChange={e => setForm({ ...form, images: e.target.value.split('\n').filter(Boolean) })} className="w-full border rounded-lg px-3 py-2 mt-1 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />
          </div>

          <input placeholder="Sale Price (optional)" type="number" value={form.salePrice} onChange={e => setForm({ ...form, salePrice: e.target.value })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />

          <input placeholder="Stock Quantity" type="number" value={form.stockQuantity} onChange={e => setForm({ ...form, stockQuantity: e.target.value })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />

          <input placeholder="Weight (e.g., 500g)" value={form.weight} onChange={e => setForm({ ...form, weight: e.target.value })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />

          <div className="flex gap-2">
            <input placeholder="Length (cm)" type="number" value={form.dimensions.length} onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, length: e.target.value } })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />
            <input placeholder="Width (cm)" type="number" value={form.dimensions.width} onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, width: e.target.value } })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />
            <input placeholder="Height (cm)" type="number" value={form.dimensions.height} onChange={e => setForm({ ...form, dimensions: { ...form.dimensions, height: e.target.value } })} className="border rounded-lg px-3 py-2 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />
          </div>
        </div>

        <div className="mt-6 flex items-center justify-end gap-3">
          <button type="button" onClick={onClose} className="px-4 py-2 border rounded-md hover:bg-gray-50">Cancel</button>
          <button type="submit" className="px-5 py-2 bg-emerald-600 text-white rounded-md hover:bg-emerald-700">Save</button>
        </div>
      </form>
    </div>
  );
}

/* -------------------- Cart Drawer -------------------- */
function CartDrawer() {
  const { items, isOpen, setIsOpen, remove, changeQty, total, clear } = useCart();

  return (
    // drawer uses CSS transitions for slide in/out
    <div className={`fixed right-0 top-0 bottom-0 w-full md:w-96 z-50 pointer-events-auto transform ${isOpen ? 'translate-x-0' : 'translate-x-full'} transition-transform duration-300`}>
      <div className="h-full bg-white shadow-2xl flex flex-col">
        <div className="p-4 border-b flex items-center justify-between">
          <div className="flex items-center gap-3">
            <ShoppingCart />
            <div>
              <div className="font-semibold">Your Cart</div>
              <div className="text-sm text-gray-500">{items.length} items</div>
            </div>
          </div>
          <button onClick={() => setIsOpen(false)} className="p-2 hover:bg-gray-100 rounded-full"><X /></button>
        </div>

        <div className="flex-1 overflow-y-auto p-4 space-y-3">
          {items.length === 0 ? (
            <div className="text-center text-gray-400 mt-10">Your cart is empty. Add items to get started.</div>
          ) : (
            items.map(it => (
              <div key={it._id} className="flex items-center gap-3 border rounded-lg p-3">
                <div className="w-16 h-16 bg-gray-50 rounded overflow-hidden grid place-items-center">
                  {it.images && it.images[0] ? <img src={it.images[0]} alt={it.name} className="object-contain w-full h-full" /> : <div className="text-gray-300">No Image</div>}
                </div>
                <div className="flex-1">
                  <div className="font-medium">{it.name}</div>
                  <div className="text-sm text-gray-500">{it.salePrice ?? it.regularPrice}</div>
                  <div className="mt-2 flex items-center gap-2">
                    <input type="number" min={1} value={it.qty} onChange={e => changeQty(it._id, Number(e.target.value))} className="w-20 border rounded px-2 py-1 text-sm bg-gray-50 focus:outline-none focus:ring-2 focus:ring-emerald-200 transition" />
                    <button onClick={() => remove(it._id)} className="text-sm text-rose-600 hover:text-rose-700">Remove</button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        <div className="p-4 border-t">
          <div className="flex items-center justify-between text-lg font-semibold">Total <span>{total.toFixed(2)}</span></div>
          <div className="mt-3 flex items-center gap-2">
            <button onClick={() => alert('Proceed to checkout flow')} disabled={items.length===0} className="flex-1 py-2 bg-emerald-600 text-white rounded-md disabled:opacity-50 disabled:cursor-not-allowed hover:bg-emerald-700">Checkout</button>
            <button onClick={clear} className="px-3 py-2 border rounded-md hover:bg-gray-50">Clear</button>
          </div>
        </div>
      </div>
    </div>
  );
}
