// Item Form Component - Updated version
const ItemForm = ({ item, onSubmit, onCancel, isLoading }) => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    regularPrice: "",
    salePrice: "",
    stockStatus: "In Stock",
    stockQuantity: "",
    weight: "",
    dimensions: {
      length: "",
      width: "",
      height: "",
    },
  });
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [filePreviews, setFilePreviews] = useState([]);
  const [existingImages, setExistingImages] = useState([]); // New state for existing images
  const [imagesToRemove, setImagesToRemove] = useState([]); // Track images to remove

  useEffect(() => {
    if (item) {
      setFormData({
        name: item.name || "",
        description: item.description || "",
        regularPrice: item.regularPrice || "",
        salePrice: item.salePrice || "",
        stockStatus: item.stockStatus || "In Stock",
        stockQuantity: item.stockQuantity || "",
        weight: item.weight || "",
        dimensions: {
          length: item.dimensions?.length || "",
          width: item.dimensions?.width || "",
          height: item.dimensions?.height || "",
        },
      });
      
      // Set existing images when editing
      if (item.images && item.images.length > 0) {
        setExistingImages(item.images);
      } else {
        setExistingImages([]);
      }
      setImagesToRemove([]); // Reset images to remove
    } else {
      // Reset states when creating new item
      setExistingImages([]);
      setImagesToRemove([]);
    }
  }, [item]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("dimensions.")) {
      const field = name.split(".")[1];
      setFormData((prev) => ({
        ...prev,
        dimensions: {
          ...prev.dimensions,
          [field]: value,
        },
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
    
    // Create previews
    const previews = files.map(file => {
      const reader = new FileReader();
      return new Promise((resolve) => {
        reader.onload = (e) => resolve({ name: file.name, url: e.target.result });
        reader.readAsDataURL(file);
      });
    });
    
    Promise.all(previews).then(setFilePreviews);
  };

  const removeFile = (index) => {
    const newFiles = selectedFiles.filter((_, i) => i !== index);
    const newPreviews = filePreviews.filter((_, i) => i !== index);
    setSelectedFiles(newFiles);
    setFilePreviews(newPreviews);
  };

  // New function to handle existing image removal
  const removeExistingImage = (imageIndex) => {
    const imageToRemove = existingImages[imageIndex];
    setImagesToRemove(prev => [...prev, imageToRemove]);
    setExistingImages(prev => prev.filter((_, i) => i !== imageIndex));
  };

  // New function to restore existing image
  const restoreExistingImage = (image) => {
    setImagesToRemove(prev => prev.filter(img => img._id !== image._id));
    setExistingImages(prev => [...prev, image]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const cleanedData = {
      ...formData,
      regularPrice: Number(formData.regularPrice),
      salePrice: formData.salePrice ? Number(formData.salePrice) : undefined,
      stockQuantity: Number(formData.stockQuantity),
      dimensions: {
        length: formData.dimensions.length
          ? Number(formData.dimensions.length)
          : undefined,
        width: formData.dimensions.width
          ? Number(formData.dimensions.width)
          : undefined,
        height: formData.dimensions.height
          ? Number(formData.dimensions.height)
          : undefined,
      },
      // Include image management data for editing
      imagesToRemove: imagesToRemove.map(img => img._id || img.url), // Send IDs or URLs to remove
      existingImages: existingImages, // Keep track of remaining existing images
    };
    onSubmit(cleanedData, selectedFiles);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Name *
          </label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Regular Price *
          </label>
          <input
            type="number"
            name="regularPrice"
            value={formData.regularPrice}
            onChange={handleChange}
            required
            min="0"
            step="0.01"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Description
        </label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          rows="3"
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Sale Price
          </label>
          <input
            type="number"
            name="salePrice"
            value={formData.salePrice}
            onChange={handleChange}
            min="0"
            step="0.01"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Stock Status
          </label>
          <select
            name="stockStatus"
            value={formData.stockStatus}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="In Stock">In Stock</option>
            <option value="Out of Stock">Out of Stock</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Stock Quantity
          </label>
          <input
            type="number"
            name="stockQuantity"
            value={formData.stockQuantity}
            onChange={handleChange}
            min="0"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Weight
        </label>
        <input
          type="text"
          name="weight"
          value={formData.weight}
          onChange={handleChange}
          placeholder="e.g., 1.5kg"
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Dimensions (cm)
        </label>
        <div className="grid grid-cols-3 gap-4">
          <input
            type="number"
            name="dimensions.length"
            value={formData.dimensions.length}
            onChange={handleChange}
            placeholder="Length"
            min="0"
            step="0.1"
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="number"
            name="dimensions.width"
            value={formData.dimensions.width}
            onChange={handleChange}
            placeholder="Width"
            min="0"
            step="0.1"
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="number"
            name="dimensions.height"
            value={formData.dimensions.height}
            onChange={handleChange}
            placeholder="Height"
            min="0"
            step="0.1"
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Images
        </label>
        
        {/* Existing Images Section */}
        {existingImages.length > 0 && (
          <div className="mb-4">
            <h4 className="text-sm font-medium text-gray-600 mb-2">Current Images</h4>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-4">
              {existingImages.map((image, index) => (
                <div key={`existing-${index}`} className="relative">
                  <img
                    src={image.url}
                    alt={`Existing ${index + 1}`}
                    className="w-full h-24 object-cover rounded-lg border"
                  />
                  <button
                    type="button"
                    onClick={() => removeExistingImage(index)}
                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                  >
                    <X size={12} />
                  </button>
                  <p className="text-xs text-gray-500 mt-1 truncate">Existing Image</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Images to Remove Section (for reference) */}
        {imagesToRemove.length > 0 && (
          <div className="mb-4">
            <h4 className="text-sm font-medium text-red-600 mb-2">Images to be removed</h4>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-4">
              {imagesToRemove.map((image, index) => (
                <div key={`remove-${index}`} className="relative opacity-50">
                  <img
                    src={image.url}
                    alt={`To remove ${index + 1}`}
                    className="w-full h-24 object-cover rounded-lg border border-red-300"
                  />
                  <button
                    type="button"
                    onClick={() => restoreExistingImage(image)}
                    className="absolute -top-2 -right-2 bg-green-500 text-white rounded-full p-1 hover:bg-green-600"
                    title="Restore image"
                  >
                    <Plus size={12} />
                  </button>
                  <p className="text-xs text-red-500 mt-1 truncate">Will be removed</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* File Upload Section */}
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-4">
          <input
            type="file"
            multiple
            accept="image/*"
            onChange={handleFileChange}
            className="w-full"
          />
          <div className="mt-2 text-sm text-gray-500">
            <Upload className="inline w-4 h-4 mr-1" />
            Upload new images (JPEG, PNG, GIF, WebP - Max 5MB each)
          </div>
        </div>
        
        {/* New File Previews */}
        {filePreviews.length > 0 && (
          <div className="mt-4">
            <h4 className="text-sm font-medium text-gray-600 mb-2">New Images to Upload</h4>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {filePreviews.map((preview, index) => (
                <div key={`new-${index}`} className="relative">
                  <img
                    src={preview.url}
                    alt={preview.name}
                    className="w-full h-24 object-cover rounded-lg border border-green-300"
                  />
                  <button
                    type="button"
                    onClick={() => removeFile(index)}
                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                  >
                    <X size={12} />
                  </button>
                  <p className="text-xs text-green-600 mt-1 truncate">{preview.name}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <div className="flex gap-2 pt-4">
        <button
          type="submit"
          disabled={isLoading}
          className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
        >
          <Save size={16} />
          {isLoading ? "Saving..." : "Save Item"}
        </button>
        <button
          type="button"
          onClick={onCancel}
          className="flex items-center gap-2 px-4 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600"
        >
          <X size={16} />
          Cancel
        </button>
      </div>
    </form>
  );
};
