import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

/**
 * customer.js
 * Advanced single-file React component for managing Customers (CRUD) with file upload.
 * Features:
 * - Responsive Tailwind UI (no external CSS required)
 * - List view with search, sort and pagination
 * - Add / Edit modal with client-side validation
 * - Image drag & drop + preview + upload progress
 * - Delete with confirmation and optimistic update
 * - Detail view modal
 * - Toast notifications
 * - Accessible controls and keyboard-friendly modals
 *
 * NOTE: Adjust API_BASE to your backend URL (e.g. http://localhost:3000)
 * This component expects the API routes exactly as described in your server:
 * POST   /api/customers (multipart/form-data key: profileImage)
 * GET    /api/customers
 * GET    /api/customers/:id
 * PUT    /api/customers/:id (multipart/form-data key: profileImage)
 * DELETE /api/customers/:id
 */

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:3000";
const PAGE_SIZE = 8;

function useToasts() {
  const [toasts, setToasts] = useState([]);
  function push(message, type = "info", ttl = 4000) {
    const id = Date.now() + Math.random();
    setToasts((t) => [...t, { id, message, type }]);
    setTimeout(() => setToasts((t) => t.filter((x) => x.id !== id)), ttl);
  }
  return { toasts, push, setToasts };
}

export default function Customers() {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [query, setQuery] = useState("");
  const [sortBy, setSortBy] = useState({ key: "name", dir: "asc" });
  const [page, setPage] = useState(1);

  const [showAddEdit, setShowAddEdit] = useState(false);
  const [editing, setEditing] = useState(null);
  const [showView, setShowView] = useState(false);
  const [viewing, setViewing] = useState(null);

  const [uploadProgress, setUploadProgress] = useState(0);

  const { toasts, push } = useToasts();

  useEffect(() => {
    fetchCustomers();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  async function fetchCustomers() {
    try {
      setLoading(true);
      const res = await axios.get(`${API_BASE}/api/customers`);
      setCustomers(res.data || []);
      setLoading(false);
    } catch (err) {
      setLoading(false);
      setError(err.message || "Failed to load customers");
      push("Failed to load customers", "error");
    }
  }

  // Filtering / sorting / paging derived state
  const filtered = customers.filter((c) => {
    if (!query) return true;
    const q = query.toLowerCase();
    return (
      (c.name || "").toLowerCase().includes(q) ||
      (c.email || "").toLowerCase().includes(q) ||
      (c.phone || "").toLowerCase().includes(q)
    );
  });

  const sorted = filtered.sort((a, b) => {
    const ka = (a[sortBy.key] || "").toString().toLowerCase();
    const kb = (b[sortBy.key] || "").toString().toLowerCase();
    if (ka < kb) return sortBy.dir === "asc" ? -1 : 1;
    if (ka > kb) return sortBy.dir === "asc" ? 1 : -1;
    return 0;
  });

  const totalPages = Math.max(1, Math.ceil(sorted.length / PAGE_SIZE));
  const pageItems = sorted.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);

  useEffect(() => {
    if (page > totalPages) setPage(totalPages);
  }, [totalPages, page]);

  function changeSort(key) {
    setSortBy((s) => ({ key, dir: s.key === key && s.dir === "asc" ? "desc" : "asc" }));
  }

  // Add / Edit form state
  const initialForm = { name: "", email: "", phone: "", address: "", profileImageFile: null, profilePreview: null };
  const [form, setForm] = useState(initialForm);
  const fileInputRef = useRef();

  function openAdd() {
    setEditing(null);
    setForm(initialForm);
    setUploadProgress(0);
    setShowAddEdit(true);
  }

  function openEdit(customer) {
    setEditing(customer);
    setForm({
      name: customer.name || "",
      email: customer.email || "",
      phone: customer.phone || "",
      address: customer.address || "",
      profileImageFile: null,
      profilePreview: customer.profileImageUrl || null,
    });
    setUploadProgress(0);
    setShowAddEdit(true);
  }

  function openView(customer) {
    setViewing(customer);
    setShowView(true);
  }

  function handleDrop(e) {
    e.preventDefault();
    const f = e.dataTransfer.files && e.dataTransfer.files[0];
    if (f) handleFile(f);
  }

  function handleFile(f) {
    // simple validation
    if (!f.type.startsWith("image/")) {
      push("Only image uploads are allowed", "error");
      return;
    }
    const reader = new FileReader();
    reader.onload = (ev) => {
      setForm((s) => ({ ...s, profileImageFile: f, profilePreview: ev.target.result }));
    };
    reader.readAsDataURL(f);
  }

  function handleInputChange(e) {
    const { name, value } = e.target;
    setForm((s) => ({ ...s, [name]: value }));
  }

  function validateForm() {
    if (!form.name || form.name.trim().length < 2) return "Name must be at least 2 characters";
    if (!form.email || !/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(form.email)) return "Provide a valid email";
    if (!form.phone || form.phone.trim().length < 7) return "Provide a valid phone number";
    return null;
  }

  async function submitForm(e) {
    e && e.preventDefault();
    const errMsg = validateForm();
    if (errMsg) return push(errMsg, "error");

    const data = new FormData();
    data.append("name", form.name);
    data.append("email", form.email);
    data.append("phone", form.phone);
    data.append("address", form.address);
    if (form.profileImageFile) data.append("profileImage", form.profileImageFile);

    try {
      setLoading(true);
      setUploadProgress(0);
      let res;
      if (editing) {
        res = await axios.put(`${API_BASE}/api/customers/${editing._id}`, data, {
          headers: { "Content-Type": "multipart/form-data" },
          onUploadProgress: (p) => setUploadProgress(Math.round((p.loaded / p.total) * 100)),
        });
        // optimistic update
        setCustomers((prev) => prev.map((c) => (c._id === res.data._id ? res.data : c)));
        push("Customer updated", "success");
      } else {
        res = await axios.post(`${API_BASE}/api/customers`, data, {
          headers: { "Content-Type": "multipart/form-data" },
          onUploadProgress: (p) => setUploadProgress(Math.round((p.loaded / p.total) * 100)),
        });
        setCustomers((prev) => [res.data, ...prev]);
        push("Customer added", "success");
      }
      setShowAddEdit(false);
      setEditing(null);
      setForm(initialForm);
      setUploadProgress(0);
      setLoading(false);
    } catch (err) {
      setLoading(false);
      push((err.response && err.response.data && err.response.data.error) || err.message || "Save failed", "error");
    }
  }

  async function removeCustomer(customer) {
    if (!window.confirm(`Delete ${customer.name || "this customer"}? This is permanent.`)) return;
    // optimistic UI
    const old = customers;
    setCustomers((c) => c.filter((x) => x._id !== customer._id));
    try {
      await axios.delete(`${API_BASE}/api/customers/${customer._id}`);
      push("Customer deleted", "success");
    } catch (err) {
      setCustomers(old);
      push("Delete failed", "error");
    }
  }

  // Small presentational helpers
  function Avatar({ src, name, size = 10 }) {
    return (
      <div className={`inline-flex items-center justify-center rounded-full overflow-hidden bg-gray-100 ${"w-" + size + " h-" + size}`}>
        {src ? (
          <img src={src} alt={name} className="w-full h-full object-cover" />
        ) : (
          <span className="text-sm text-gray-600">{(name || "?").slice(0, 2).toUpperCase()}</span>
        )}
      </div>
    );
  }

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-white to-gray-50">
      <div className="max-w-6xl mx-auto">
        <header className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-semibold text-gray-800">Customers</h1>
          <div className="flex items-center gap-3">
            <div className="relative">
              <input
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Search by name, email or phone..."
                className="border border-gray-200 rounded-md py-2 px-3 w-72 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-300"
              />
              {query && (
                <button
                  onClick={() => setQuery("")}
                  className="absolute right-1 top-1 text-xs px-2 py-1 text-gray-500"
                >
                  Clear
                </button>
              )}
            </div>
            <button
              onClick={openAdd}
              className="inline-flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-md shadow-sm"
            >
              + Add Customer
            </button>
          </div>
        </header>

        <section className="bg-white rounded-2xl shadow p-4">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-3">
              <span className="text-sm text-gray-600">Sort by:</span>
              <select
                value={sortBy.key}
                onChange={(e) => changeSort(e.target.value)}
                className="border border-gray-200 rounded-md py-1 px-2"
              >
                <option value="name">Name</option>
                <option value="email">Email</option>
                <option value="phone">Phone</option>
              </select>
              <button
                onClick={() => setSortBy((s) => ({ ...s, dir: s.dir === "asc" ? "desc" : "asc" }))}
                className="px-2 py-1 border border-gray-200 rounded-md"
              >
                {sortBy.dir === "asc" ? "▲" : "▼"}
              </button>
            </div>

            <div className="text-sm text-gray-500">Total: {customers.length}</div>
          </div>

          {/* Table / Grid */}
          <div className="overflow-x-auto">
            <table className="w-full table-auto text-left">
              <thead>
                <tr className="text-sm text-gray-500 border-b">
                  <th className="p-3">Customer</th>
                  <th className="p-3">Contact</th>
                  <th className="p-3">Address</th>
                  <th className="p-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan={4} className="p-6 text-center text-gray-500">
                      Loading...
                    </td>
                  </tr>
                ) : pageItems.length === 0 ? (
                  <tr>
                    <td colSpan={4} className="p-6 text-center text-gray-500">
                      No customers found.
                    </td>
                  </tr>
                ) : (
                  pageItems.map((c) => (
                    <tr key={c._id} className="hover:bg-gray-50 transition">
                      <td className="p-3 align-top">
                        <div className="flex items-center gap-3">
                          <Avatar src={c.profileImageUrl} name={c.name} size={12} />
                          <div>
                            <div className="font-medium text-gray-800">{c.name}</div>
                            <div className="text-xs text-gray-500">ID: {c._id}</div>
                          </div>
                        </div>
                      </td>
                      <td className="p-3 align-top">
                        <div className="text-sm">{c.email}</div>
                        <div className="text-xs text-gray-500">{c.phone}</div>
                      </td>
                      <td className="p-3 align-top text-sm text-gray-600">{c.address || "—"}</td>
                      <td className="p-3 align-top">
                        <div className="flex items-center gap-2">
                          <button
                            onClick={() => openView(c)}
                            className="px-3 py-1 border rounded-md text-sm text-indigo-600"
                          >
                            View
                          </button>
                          <button
                            onClick={() => openEdit(c)}
                            className="px-3 py-1 border rounded-md text-sm text-amber-600"
                          >
                            Edit
                          </button>
                          <button
                            onClick={() => removeCustomer(c)}
                            className="px-3 py-1 border rounded-md text-sm text-red-600"
                          >
                            Delete
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          <div className="mt-4 flex items-center justify-between">
            <div className="text-sm text-gray-600">
              Showing {(page - 1) * PAGE_SIZE + 1} - {Math.min(page * PAGE_SIZE, sorted.length)} of {sorted.length}
            </div>
            <div className="flex items-center gap-2">
              <button
                onClick={() => setPage((p) => Math.max(1, p - 1))}
                className="px-3 py-1 border rounded-md"
                disabled={page === 1}
              >
                Prev
              </button>
              <div className="text-sm px-2">Page {page} / {totalPages}</div>
              <button
                onClick={() => setPage((p) => Math.min(totalPages, p + 1))}
                className="px-3 py-1 border rounded-md"
                disabled={page === totalPages}
              >
                Next
              </button>
            </div>
          </div>
        </section>

        {/* Add / Edit Modal */}
        {showAddEdit && (
          <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
            <div className="absolute inset-0 bg-black/40" onClick={() => setShowAddEdit(false)} />
            <form
              onSubmit={submitForm}
              className="relative bg-white rounded-2xl shadow-lg max-w-3xl w-full p-6 z-10"
              onKeyDown={(e) => {
                if (e.key === "Escape") setShowAddEdit(false);
              }}
            >
              <div className="flex items-start justify-between gap-4 mb-4">
                <h2 className="text-lg font-semibold">{editing ? "Edit Customer" : "Add Customer"}</h2>
                <div className="flex items-center gap-2">
                  {uploadProgress > 0 && uploadProgress < 100 && (
                    <div className="text-sm text-gray-500">Uploading: {uploadProgress}%</div>
                  )}
                  <button type="button" onClick={() => setShowAddEdit(false)} className="text-gray-500">✕</button>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-xs text-gray-600 mb-1">Full name</label>
                  <input name="name" value={form.name} onChange={handleInputChange} className="w-full border rounded-md p-2" />
                </div>
                <div>
                  <label className="block text-xs text-gray-600 mb-1">Email</label>
                  <input name="email" value={form.email} onChange={handleInputChange} className="w-full border rounded-md p-2" />
                </div>

                <div>
                  <label className="block text-xs text-gray-600 mb-1">Phone</label>
                  <input name="phone" value={form.phone} onChange={handleInputChange} className="w-full border rounded-md p-2" />
                </div>
                <div>
                  <label className="block text-xs text-gray-600 mb-1">Address</label>
                  <input name="address" value={form.address} onChange={handleInputChange} className="w-full border rounded-md p-2" />
                </div>

                <div className="md:col-span-2">
                  <label className="block text-xs text-gray-600 mb-2">Profile image</label>
                  <div
                    onDragOver={(e) => e.preventDefault()}
                    onDrop={handleDrop}
                    className="border-2 border-dashed border-gray-200 rounded-md p-4 flex items-center gap-4"
                  >
                    <div className="w-28 h-28 rounded-lg overflow-hidden bg-gray-50 flex items-center justify-center">
                      {form.profilePreview ? (
                        <img src={form.profilePreview} alt="preview" className="w-full h-full object-cover" />
                      ) : (
                        <div className="text-sm text-gray-400">Drag image here</div>
                      )}
                    </div>

                    <div className="flex-1">
                      <p className="text-sm text-gray-600">PNG, JPG up to 5MB. Drag & drop or click to choose file.</p>
                      <div className="mt-3 flex items-center gap-2">
                        <input
                          ref={fileInputRef}
                          type="file"
                          accept="image/*"
                          onChange={(e) => e.target.files && handleFile(e.target.files[0])}
                          className="hidden"
                        />
                        <button
                          type="button"
                          onClick={() => fileInputRef.current && fileInputRef.current.click()}
                          className="px-3 py-1 border rounded-md"
                        >
                          Choose file
                        </button>
                        {form.profilePreview && (
                          <button
                            type="button"
                            onClick={() => setForm((s) => ({ ...s, profilePreview: null, profileImageFile: null }))}
                            className="px-3 py-1 border rounded-md text-sm text-red-600"
                          >
                            Remove
                          </button>
                        )}
                      </div>
                      {form.profileImageFile && (
                        <div className="mt-2 text-xs text-gray-500">Selected: {form.profileImageFile.name} ({Math.round(form.profileImageFile.size/1024)} KB)</div>
                      )}
                    </div>
                  </div>
                </div>
              </div>

              <div className="mt-4 flex items-center justify-end gap-3">
                <button type="button" onClick={() => { setShowAddEdit(false); setEditing(null); }} className="px-4 py-2 border rounded-md">Cancel</button>
                <button type="submit" className="px-4 py-2 bg-indigo-600 text-white rounded-md">{editing ? "Update" : "Create"}</button>
              </div>
            </form>
          </div>
        )}

        {/* View Modal */}
        {showView && viewing && (
          <div className="fixed inset-0 z-40 flex items-center justify-center p-4">
            <div className="absolute inset-0 bg-black/40" onClick={() => setShowView(false)} />
            <div className="relative bg-white rounded-2xl shadow-lg max-w-2xl w-full p-6 z-10">
              <div className="flex items-start justify-between mb-4">
                <h3 className="text-lg font-semibold">Customer details</h3>
                <button onClick={() => setShowView(false)} className="text-gray-500">✕</button>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="col-span-1 flex items-center justify-center">
                  <div className="w-40 h-40 rounded-lg overflow-hidden bg-gray-50">
                    {viewing.profileImageUrl ? (
                      <img src={viewing.profileImageUrl} alt={viewing.name} className="w-full h-full object-cover" />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center text-gray-400">No photo</div>
                    )}
                  </div>
                </div>
                <div className="md:col-span-2">
                  <div className="text-lg font-medium">{viewing.name}</div>
                  <div className="text-sm text-gray-600">{viewing.email} • {viewing.phone}</div>

                  <div className="mt-4 text-sm text-gray-700">
                    <div><strong>Address:</strong> {viewing.address || "—"}</div>
                    <div className="mt-2 text-xs text-gray-500">ID: {viewing._id}</div>
                  </div>

                  <div className="mt-4 flex gap-2">
                    <button onClick={() => { setShowView(false); openEdit(viewing); }} className="px-3 py-1 border rounded-md text-amber-600">Edit</button>
                    <button onClick={() => { setShowView(false); removeCustomer(viewing); }} className="px-3 py-1 border rounded-md text-red-600">Delete</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Toasts */}
        <div className="fixed right-4 bottom-6 z-50 space-y-2">
          {toasts.map((t) => (
            <div key={t.id} className={`px-4 py-2 rounded-md shadow ${t.type === "error" ? "bg-red-100 text-red-800" : t.type === "success" ? "bg-green-100 text-green-800" : "bg-gray-100 text-gray-800"}`}>
              {t.message}
            </div>
          ))}
        </div>
      </div>

      {/* Small inline styles to handle Tailwind dynamic sizes used in Avatar. */}
      <style>{`
        .w-10{width:40px;height:40px}
        .w-12{width:48px;height:48px}
        .h-10{height:40px}
        .h-12{height:48px}
      `}</style>
    </div>
  );
}
